        </div>

        <div className="hidden xl:flex 2xl:hidden items-center gap-2.5 flex-1">
          {mainFolders.map((folder) => {
            const Icon = folder.icon;
            const isActive = currentFolder === folder.id;

            return (
              <Button
                key={folder.id}
                variant="ghost"
