                  <span className="text-[12px]">{folder.count}</span>
                )}
              </Button>
            );
          })}

          <Separator orientation="vertical" className="h-5" />

          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button variant="ghost" size="sm" className="h-[30px] gap-1.5">
                <MenuIcon className="size-4" />
                <span className="text-[13px]">More</span>
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="start" className="w-48">
              {mainFolders.slice(3).map((folder) => {
                const Icon = folder.icon;
                const isActive = currentFolder === folder.id;

                return (
                  <DropdownMenuItem
                    key={folder.id}
                    onClick={() => setFolder(folder.id as any)}
                    className={cn(isActive && "bg-muted")}
                  >
                    <Icon className="size-4" />
                    <span>{folder.label}</span>
                  </DropdownMenuItem>
                );
              })}
              <DropdownMenuSeparator />
              {additionalFolders.map((folder) => {
                const Icon = folder.icon;
                const isActive = currentFolder === folder.id;

                return (
                  <DropdownMenuItem
                    key={folder.id}
                    onClick={() => setFolder(folder.id as any)}
                    className={cn(isActive && "bg-muted")}
                  >
                    <Icon className="size-4" />
                    <span>{folder.label}</span>
                  </DropdownMenuItem>
                );
              })}
            </DropdownMenuContent>
          </DropdownMenu>
        </div>

        <div className="hidden md:flex lg:hidden items-center gap-2 flex-1">
          {mainFolders.slice(0, 2).map((folder) => {
            const Icon = folder.icon;
            const isActive = currentFolder === folder.id;

            return (
              <Button
                key={folder.id}
                variant="ghost"
                size="sm"
                onClick={() => setFolder(folder.id as any)}
                className={cn(
                  "h-[30px] gap-1.5",
                  isActive && "bg-muted text-foreground hover:bg-muted"
                )}
              >
                <Icon className="size-4" />
                <span className="text-[13px]">{folder.label}</span>
              </Button>
            );
          })}

          <Separator orientation="vertical" className="h-5" />

          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button variant="ghost" size="sm" className="h-[30px] gap-1.5">
                <MenuIcon className="size-4" />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="start" className="w-48">
              {mainFolders.slice(2).map((folder) => {
                const Icon = folder.icon;
                const isActive = currentFolder === folder.id;

                return (
                  <DropdownMenuItem
                    key={folder.id}
                    onClick={() => setFolder(folder.id as any)}
                    className={cn(isActive && "bg-muted")}
                  >
                    <Icon className="size-4" />
                    <span>{folder.label}</span>
                    {folder.count && isActive && (
                      <span className="ml-auto text-xs text-muted-foreground">
                        {folder.count}
                      </span>
                    )}
                  </DropdownMenuItem>
                );
              })}
              <DropdownMenuSeparator />
              {additionalFolders.map((folder) => {
                const Icon = folder.icon;
                const isActive = currentFolder === folder.id;

                return (
                  <DropdownMenuItem
                    key={folder.id}
                    onClick={() => setFolder(folder.id as any)}
                    className={cn(isActive && "bg-muted")}
                  >
                    <Icon className="size-4" />
                    <span>{folder.label}</span>
                  </DropdownMenuItem>
                );
              })}
            </DropdownMenuContent>
          </DropdownMenu>
        </div>

        <div className="flex md:hidden items-center gap-2 flex-1">
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button variant="ghost" size="sm" className="h-[30px] gap-1.5">
                <MenuIcon className="size-4" />
                <span className="text-[13px]">Folders</span>
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="start" className="w-48">
              {mainFolders.map((folder) => {
                const Icon = folder.icon;
                const isActive = currentFolder === folder.id;

                return (
                  <DropdownMenuItem
                    key={folder.id}
                    onClick={() => setFolder(folder.id as any)}
                    className={cn(isActive && "bg-muted")}
                  >
                    <Icon className="size-4" />
                    <span>{folder.label}</span>
                    {folder.count && isActive && (
                      <span className="ml-auto text-xs text-muted-foreground">
                        {folder.count}
                      </span>
                    )}
                  </DropdownMenuItem>
                );
              })}
              <DropdownMenuSeparator />
              {additionalFolders.map((folder) => {
                const Icon = folder.icon;
                const isActive = currentFolder === folder.id;

                return (
                  <DropdownMenuItem
                    key={folder.id}
                    onClick={() => setFolder(folder.id as any)}
                    className={cn(isActive && "bg-muted")}
                  >
                    <Icon className="size-4" />
                    <span>{folder.label}</span>
                  </DropdownMenuItem>
                );
              })}
            </DropdownMenuContent>
          </DropdownMenu>
        </div>
      </div>
    </div>
  );
}

"use client"

import * as React from "react"
import * as AvatarPrimitive from "@radix-ui/react-avatar"

