import { TaskSidebar } from '@/components/task/sidebar/task-sidebar';
import { TaskHeader } from '@/components/task/header/task-header';
import { TaskBoard } from '@/components/task/board/task-board';
import { SidebarProvider } from '@/components/ui/sidebar';

export default function Home() {
  return (
    <SidebarProvider>
      <TaskSidebar />
      <div className="flex-1 flex flex-col overflow-hidden h-screen">
        <TaskHeader />
        <main className="w-full h-full overflow-x-auto">
          <TaskBoard />
        </main>
      </div>
    </SidebarProvider>
  );
}

'use client';

import { useTasksStore } from '@/store/tasks-store';
import { statuses } from '@/mock-data/statuses';
import { TaskColumn } from './task-column';

export function TaskBoard() {
   const { tasksByStatus } = useTasksStore();

   return (
     <div className="flex h-full gap-3 px-3 pt-4 pb-2 min-w-max overflow-hidden">
       {statuses.map((status) => (
         <TaskColumn
           key={status.id}
           status={status}
           tasks={tasksByStatus[status.id] || []}
         />
       ))}
     </div>
   );
}

"use client";

import { Task } from "@/mock-data/tasks";
import {
  Calendar,
  MessageSquare,
  FileText,
  Link,
  CheckCircle,
  InfoIcon,
  Hexagon,
  Stars,
} from "lucide-react";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { cn } from "@/lib/utils";
import { CircularProgressbar, buildStyles } from "react-circular-progressbar";
import "react-circular-progressbar/dist/styles.css";

interface TaskCardProps {
  task: Task;
}

export function TaskCard({ task }: TaskCardProps) {
  const StatusIcon = task.status.icon;
  const hasProgress = task.progress.total > 0;
  const isCompleted =
    task.progress.completed === task.progress.total && hasProgress;

  return (
    <div className="bg-background shrink-0 rounded-lg overflow-hidden border border-border">
      <div className="px-3 py-2.5">
        <div className="flex items-center gap-2 mb-2">
          <div className="size-5 mt-0.5 shrink-0 flex items-center justify-center bg-muted rounded-sm p-1">
            <StatusIcon />
          </div>
          <h3 className="text-sm font-medium leading-tight flex-1">
            {task.title}
          </h3>
          {task.priority === "urgent" && !isCompleted && (
            <Stars className="size-4 shrink-0 text-pink-500" />
          )}
          {task.priority === "high" && !isCompleted && (
            <InfoIcon className="size-4 shrink-0 text-red-500" />
          )}
          {task.priority === "medium" && !isCompleted && (
            <Hexagon className="size-4 shrink-0 text-cyan-500" />
          )}
          {isCompleted && (
            <CheckCircle className="size-4 shrink-0 text-green-500" />
          )}
        </div>

        <p className="text-xs text-muted-foreground mb-3 line-clamp-2">
          {task.description}
        </p>

        {task.labels.length > 0 && (
          <div className="flex flex-wrap gap-1.5">
            {task.labels.map((label) => (
              <Badge
                key={label.id}
                variant="secondary"
                className={cn(
                  "text-[10px] px-1.5 py-0.5 font-medium",
                  label.color
                )}
              >
                {label.name}
              </Badge>
            ))}
          </div>
        )}
      </div>

      <div className="px-3 py-2.5 border-t border-border border-dashed">
        <div className="flex items-center justify-between flex-wrap gap-2">
          <div className="flex items-center gap-3 text-xs text-muted-foreground flex-wrap">
            {task.date && (
              <div className="flex items-center gap-1.5 border border-border rounded-sm py-1 px-2">
                <Calendar className="size-3" />
                <span>{task.date}</span>
              </div>
            )}
            {task.comments > 0 && (
              <div className="flex items-center gap-1.5 border border-border rounded-sm py-1 px-2">
                <MessageSquare className="size-3" />
                <span>{task.comments}</span>
              </div>
            )}
            {task.attachments > 0 && (
              <div className="flex items-center gap-1.5 border border-border rounded-sm py-1 px-2">
                <FileText className="size-3" />
                <span>{task.attachments}</span>
              </div>
            )}
            {task.links > 0 && (
              <div className="flex items-center gap-1.5 border border-border rounded-sm py-1 px-2">
                <Link className="size-3" />
                <span>{task.links}</span>
              </div>
            )}
            {hasProgress && (
              <div className="flex items-center gap-1.5 border border-border rounded-sm py-1 px-2">
                {isCompleted ? (
                  <CheckCircle className="size-3 text-green-500" />
                ) : (
                  <div className="size-3">
                    <CircularProgressbar
                      value={
                        (task.progress.completed / task.progress.total) * 100
                      }
                      strokeWidth={12}
                      styles={buildStyles({
                        pathColor: "#10b981",
                        trailColor: "#EDEDED",
                        strokeLinecap: "round",
                      })}
                    />
                  </div>
                )}
                <span>
                  {task.progress.completed}/{task.progress.total}
                </span>
              </div>
            )}
          </div>

          {task.assignees.length > 0 && (
            <div className="flex -space-x-2">
              {task.assignees.map((user) => (
                <Avatar
                  key={user.id}
                  className="size-5 border-2 border-background"
                >
                  <AvatarImage src={user.avatar} alt={user.name} />
                  <AvatarFallback className="text-[10px]">
                    {user.name
                      .split(" ")
                      .map((n) => n[0])
                      .join("")}
                  </AvatarFallback>
                </Avatar>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

"use client";

import { Status } from "@/mock-data/statuses";
import { Task } from "@/mock-data/tasks";
import { TaskCard } from "./task-card";
import { Plus, MoreHorizontal } from "lucide-react";
import { Button } from "@/components/ui/button";
import { cn } from "@/lib/utils";

interface TaskColumnProps {
  status: Status;
  tasks: Task[];
}

export function TaskColumn({ status, tasks }: TaskColumnProps) {
  const StatusIcon = status.icon;

  return (
    <div className="shrink-0 w-[300px] lg:w-[360px] flex flex-col h-full flex-1">
      <div className="rounded-lg border border-border p-3 bg-muted/70 dark:bg-muted/50 flex flex-col max-h-full">
        <div className="flex items-center justify-between mb-2 rounded-lg">
          <div className="flex items-center gap-2">
            <div className="size-4 flex items-center justify-center">
              <StatusIcon />
            </div>
            <span className="text-sm font-medium">{status.name}</span>
          </div>
          <div className="flex items-center gap-1">
            <Button variant="ghost" size="icon" className="h-6 w-6">
              <Plus className="size-4" />
            </Button>
            <Button variant="ghost" size="icon" className="h-6 w-6">
              <MoreHorizontal className="size-4" />
            </Button>
          </div>
        </div>

        <div className="flex flex-col gap-3 overflow-y-auto h-full">
          {tasks.map((task) => (
            <TaskCard key={task.id} task={task} />
          ))}

          <Button
            variant="ghost"
            size="sm"
            className="gap-2 text-xs h-auto py-1 px-0 self-start hover:bg-background"
          >
            <Plus className="size-4" />
            <span>Add task</span>
          </Button>
        </div>
      </div>
    </div>
  );
}

"use client";

import * as React from "react";
import {
  SlidersHorizontal,
  Check,
  Layers,
  Stars,
  InfoIcon,
  Hexagon,
  Minus,
  Users,
  User,
  UserX,
} from "lucide-react";
import { Button } from "@/components/ui/button";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import { Separator } from "@/components/ui/separator";

const priorities = [
  { id: "all", name: "All priorities", icon: Layers },
  { id: "urgent", name: "Urgent", icon: Stars, color: "text-pink-500" },
  { id: "high", name: "High", icon: InfoIcon, color: "text-red-500" },
  { id: "medium", name: "Medium", icon: Hexagon, color: "text-cyan-500" },
  { id: "low", name: "Low", icon: Minus, color: "text-gray-400" },
];

const assignees = [
  { id: "all", name: "All assignees", icon: Users },
  { id: "me", name: "Assigned to me", icon: User },
  { id: "unassigned", name: "Unassigned", icon: UserX },
];

export function TaskFilters() {
  const [open, setOpen] = React.useState(false);
  const [selectedPriority, setSelectedPriority] = React.useState("all");
  const [selectedAssignee, setSelectedAssignee] = React.useState("all");

  return (
    <Popover open={open} onOpenChange={setOpen}>
      <PopoverTrigger asChild>
        <Button variant="secondary" size="sm" className="sm:gap-2">
          <SlidersHorizontal className="size-4" />
          <span className="hidden sm:inline">Filter</span>
        </Button>
      </PopoverTrigger>
      <PopoverContent className="w-72 p-4" align="start">
        <div className="space-y-4">
          <div>
            <h4 className="text-sm font-semibold mb-3 flex items-center gap-2">
              <Layers className="size-4 text-muted-foreground" />
              Priority
            </h4>
            <div className="space-y-1">
              {priorities.map((priority) => {
                const Icon = priority.icon;
                return (
                  <Button
                    key={priority.id}
                    variant="ghost"
                    size="sm"
                    className="w-full justify-between h-9 px-3"
                    onClick={() => setSelectedPriority(priority.id)}
                  >
                    <div className="flex items-center gap-2.5">
                      <Icon
                        className={`size-4 ${priority.color || "text-muted-foreground"}`}
                      />
                      <span className="text-sm">{priority.name}</span>
                    </div>
                    {selectedPriority === priority.id && (
                      <Check className="size-4 text-primary" />
                    )}
                  </Button>
                );
              })}
            </div>
          </div>

          <Separator />

          <div>
            <h4 className="text-sm font-semibold mb-3 flex items-center gap-2">
              <Users className="size-4 text-muted-foreground" />
              Assignee
            </h4>
            <div className="space-y-1">
              {assignees.map((assignee) => {
                const Icon = assignee.icon;
                return (
                  <Button
                    key={assignee.id}
                    variant="ghost"
                    size="sm"
                    className="w-full justify-between h-9 px-3"
                    onClick={() => setSelectedAssignee(assignee.id)}
                  >
                    <div className="flex items-center gap-2.5">
                      <Icon className="size-4 text-muted-foreground" />
                      <span className="text-sm">{assignee.name}</span>
                    </div>
                    {selectedAssignee === assignee.id && (
                      <Check className="size-4 text-primary" />
                    )}
                  </Button>
                );
              })}
            </div>
          </div>

          <Separator />

          <Button
            variant="outline"
            size="sm"
            className="w-full h-9"
            onClick={() => {
              setSelectedPriority("all");
              setSelectedAssignee("all");
            }}
          >
            Clear all filters
          </Button>
        </div>
      </PopoverContent>
    </Popover>
  );
}

"use client";

import * as React from "react";
import {
  ListFilter,
  Check,
  CircleDot,
  Flag,
  Calendar,
  User,
  ArrowDownAZ,
} from "lucide-react";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";

const sortOptions = [
  { id: "status", name: "Sort by status", icon: CircleDot },
  { id: "priority", name: "Sort by priority", icon: Flag },
  { id: "date", name: "Sort by date", icon: Calendar },
  { id: "assignee", name: "Sort by assignee", icon: User },
  { id: "alphabetical", name: "Sort alphabetically", icon: ArrowDownAZ },
];

export function TaskSort() {
  const [selectedSort, setSelectedSort] = React.useState("status");

  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button variant="secondary" size="sm" className="sm:gap-2">
          <ListFilter className="size-4" />
          <span className="hidden sm:inline">Sort</span>
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent align="start" className="w-56">
        <DropdownMenuLabel className="flex items-center gap-2">
          <ListFilter className="size-4" />
          Sort options
        </DropdownMenuLabel>
        <DropdownMenuSeparator />
        {sortOptions.map((option) => {
          const Icon = option.icon;
          return (
            <DropdownMenuItem
              key={option.id}
              onClick={() => setSelectedSort(option.id)}
              className="flex items-center justify-between gap-2"
            >
              <div className="flex items-center gap-2.5">
                <Icon className="size-4 text-muted-foreground" />
                <span>{option.name}</span>
              </div>
              {selectedSort === option.id && (
                <Check className="size-4 text-primary" />
              )}
            </DropdownMenuItem>
          );
        })}
      </DropdownMenuContent>
    </DropdownMenu>
  );
}

"use client";

import * as React from "react";
import { Sparkle, Zap, Clock, Mail, Bell } from "lucide-react";
import { Button } from "@/components/ui/button";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";

const automationOptions = [
  {
    id: "auto-assign",
    name: "Auto-assign tasks",
    description: "Automatically assign new tasks to team members",
    icon: Zap,
  },
  {
    id: "due-date",
    name: "Set due date reminders",
    description: "Get notified before tasks are due",
    icon: Clock,
  },
  {
    id: "email-summary",
    name: "Daily email summary",
    description: "Receive a summary of your tasks every morning",
    icon: Mail,
  },
  {
    id: "slack-notify",
    name: "Slack notifications",
    description: "Send updates to Slack when tasks change",
    icon: Bell,
  },
];

export function TaskAutomate() {
  const [open, setOpen] = React.useState(false);

  return (
    <Popover open={open} onOpenChange={setOpen}>
      <PopoverTrigger asChild>
        <Button variant="secondary" size="sm" className="sm:gap-2">
          <Sparkle className="size-4" />
          <span className="hidden sm:inline">Automate</span>
          <span className="text-xs text-purple-600 dark:text-purple-400 font-medium">
            Pro
          </span>
        </Button>
      </PopoverTrigger>
      <PopoverContent className="w-80 p-4" align="start">
        <div className="space-y-4">
          <div>
            <h3 className="text-sm font-semibold mb-1">Automation Rules</h3>
            <p className="text-xs text-muted-foreground">
              Upgrade to Pro to unlock automation features
            </p>
          </div>

          <div className="space-y-2">
            {automationOptions.map((option) => {
              const Icon = option.icon;
              return (
                <div
                  key={option.id}
                  className="flex gap-3 p-2.5 rounded-md bg-muted/50 opacity-60"
                >
                  <Icon className="size-4 shrink-0 mt-0.5 text-purple-600 dark:text-purple-400" />
                  <div className="flex-1">
                    <h4 className="text-sm font-medium">{option.name}</h4>
                    <p className="text-xs text-muted-foreground">
                      {option.description}
                    </p>
                  </div>
                </div>
              );
            })}
          </div>

          <Button className="w-full" size="sm">
            <Sparkle className="size-4" />
            Upgrade to Pro
          </Button>
        </div>
      </PopoverContent>
    </Popover>
  );
}

"use client"

import * as React from "react"
import * as AvatarPrimitive from "@radix-ui/react-avatar"

import { cn } from "@/lib/utils"

function Avatar({
  className,
  ...props
}: React.ComponentProps<typeof AvatarPrimitive.Root>) {
  return (
    <AvatarPrimitive.Root
      data-slot="avatar"
      className={cn(
        "relative flex size-8 shrink-0 overflow-hidden rounded-full",
        className
      )}
      {...props}
    />
  )
}

function AvatarImage({
  className,
  ...props
}: React.ComponentProps<typeof AvatarPrimitive.Image>) {
  return (
    <AvatarPrimitive.Image
      data-slot="avatar-image"
      className={cn("aspect-square size-full", className)}
      {...props}
    />
  )
}

function AvatarFallback({
  className,
  ...props
}: React.ComponentProps<typeof AvatarPrimitive.Fallback>) {
  return (
    <AvatarPrimitive.Fallback
      data-slot="avatar-fallback"
      className={cn(
        "bg-muted flex size-full items-center justify-center rounded-full",
        className
      )}
      {...props}
    />
  )
}

export { Avatar, AvatarImage, AvatarFallback }

import * as React from "react"
import { Slot } from "@radix-ui/react-slot"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const badgeVariants = cva(
  "inline-flex items-center justify-center rounded-full border px-2 py-0.5 text-xs font-medium w-fit whitespace-nowrap shrink-0 [&>svg]:size-3 gap-1 [&>svg]:pointer-events-none focus-visible:border-ring focus-visible:ring-ring/50 focus-visible:ring-[3px] aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive transition-[color,box-shadow] overflow-hidden",
  {
    variants: {
      variant: {
        default:
          "border-transparent bg-primary text-primary-foreground [a&]:hover:bg-primary/90",
        secondary:
          "border-transparent bg-secondary text-secondary-foreground [a&]:hover:bg-secondary/90",
        destructive:
          "border-transparent bg-destructive text-white [a&]:hover:bg-destructive/90 focus-visible:ring-destructive/20 dark:focus-visible:ring-destructive/40 dark:bg-destructive/60",
        outline:
          "text-foreground [a&]:hover:bg-accent [a&]:hover:text-accent-foreground",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  }
)

function Badge({
  className,
  variant,
  asChild = false,
  ...props
}: React.ComponentProps<"span"> &
  VariantProps<typeof badgeVariants> & { asChild?: boolean }) {
  const Comp = asChild ? Slot : "span"

  return (
    <Comp
      data-slot="badge"
      className={cn(badgeVariants({ variant }), className)}
      {...props}
    />
  )
}

export { Badge, badgeVariants }

import * as React from "react"
import { Slot } from "@radix-ui/react-slot"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const buttonVariants = cva(
  "inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium transition-all disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none [&_svg:not([class*='size-'])]:size-4 shrink-0 [&_svg]:shrink-0 outline-none focus-visible:border-ring focus-visible:ring-ring/50 focus-visible:ring-[3px] aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive",
  {
    variants: {
      variant: {
        default: "bg-[#242424] text-white hover:bg-[#242424]/90 shadow-[inset_0_-4px_1px_-1px_rgba(0,0,0,0.14),0_4px_6px_0_rgba(0,0,0,0.14)]",
        destructive:
          "bg-destructive text-white hover:bg-destructive/90 focus-visible:ring-destructive/20 dark:focus-visible:ring-destructive/40 dark:bg-destructive/60",
        outline:
          "border bg-background shadow-xs hover:bg-accent hover:text-accent-foreground dark:bg-input/30 dark:border-input dark:hover:bg-input/50",
        secondary:
          "bg-secondary text-secondary-foreground hover:bg-secondary/80",
        ghost:
          "hover:bg-accent hover:text-accent-foreground dark:hover:bg-accent/50",
        link: "text-primary underline-offset-4 hover:underline",
      },
      size: {
        default: "h-9 px-4 py-2 has-[>svg]:px-3",
        sm: "h-8 rounded-md gap-1.5 px-3 has-[>svg]:px-2.5",
        lg: "h-10 rounded-md px-6 has-[>svg]:px-4",
        icon: "size-9",
        "icon-sm": "size-8",
        "icon-lg": "size-10",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  }
)

function Button({
  className,
  variant,
  size,
  asChild = false,
  ...props
}: React.ComponentProps<"button"> &
  VariantProps<typeof buttonVariants> & {
    asChild?: boolean
  }) {
  const Comp = asChild ? Slot : "button"

  return (
    <Comp
      data-slot="button"
      className={cn(buttonVariants({ variant, size, className }))}
      {...props}
    />
  )
}

export { Button, buttonVariants }

"use client";

import * as React from "react";
import { ChevronLeft, ChevronRight } from "lucide-react";
import { DayPicker } from "react-day-picker";

import { cn } from "@/lib/utils";
import { buttonVariants } from "@/components/ui/button";

function Calendar({
  className,
  classNames,
  showOutsideDays = true,
  ...props
}: React.ComponentProps<typeof DayPicker>) {
  return (
    <DayPicker
      showOutsideDays={showOutsideDays}
      className={cn("p-3", className)}
      classNames={{
        months: "flex flex-col sm:flex-row gap-2",
        month: "flex flex-col gap-4",
        caption: "flex justify-center pt-1 relative items-center w-full",
        caption_label: "text-sm font-medium",
        nav: "flex items-center gap-1",
        nav_button: cn(
          buttonVariants({ variant: "outline" }),
          "size-7 bg-transparent p-0 opacity-50 hover:opacity-100"
        ),
        nav_button_previous: "absolute left-1",
        nav_button_next: "absolute right-1",
        table: "w-full border-collapse space-x-1",
        head_row: "flex",
        head_cell:
          "text-muted-foreground rounded-md w-8 font-normal text-[0.8rem]",
        row: "flex w-full mt-2",
        cell: cn(
          "relative p-0 text-center text-sm focus-within:relative focus-within:z-20 [&:has([aria-selected])]:bg-accent [&:has([aria-selected].day-range-end)]:rounded-r-md",
          props.mode === "range"
            ? "[&:has(>.day-range-end)]:rounded-r-md [&:has(>.day-range-start)]:rounded-l-md first:[&:has([aria-selected])]:rounded-l-md last:[&:has([aria-selected])]:rounded-r-md"
            : "[&:has([aria-selected])]:rounded-md"
        ),
        day: cn(
          buttonVariants({ variant: "ghost" }),
          "size-8 p-0 font-normal aria-selected:opacity-100"
        ),
        day_range_start:
          "day-range-start aria-selected:bg-primary aria-selected:text-primary-foreground",
        day_range_end:
          "day-range-end aria-selected:bg-primary aria-selected:text-primary-foreground",
        day_selected:
          "bg-primary text-primary-foreground hover:bg-primary hover:text-primary-foreground focus:bg-primary focus:text-primary-foreground",
        day_today: "bg-accent text-accent-foreground",
        day_outside:
          "day-outside text-muted-foreground aria-selected:text-muted-foreground",
        day_disabled: "text-muted-foreground opacity-50",
        day_range_middle:
          "aria-selected:bg-accent aria-selected:text-accent-foreground",
        day_hidden: "invisible",
        ...classNames,
      }}
      components={{
        IconLeft: ({ className, ...props }) => (
          <ChevronLeft className={cn("size-4", className)} {...props} />
        ),
        IconRight: ({ className, ...props }) => (
          <ChevronRight className={cn("size-4", className)} {...props} />
        ),
      }}
      {...props}
    />
  );
}

